import React, { useState } from "react";
import "./ExpenseForm.css";
const ExpenseForm = (props) => {
  const [userInput, setUserInput] = useState({
    enteredTitle: "",
    enteredAmount: "",
    enteredDate: "",
  });

  const titleChangeHandler = (e) => {
    setUserInput((prevState)=>
    {
      return  {...prevState,enteredTitle:e.target.value}
    })
  };

 const amountChangeHandler=(e)=>
 {
    setUserInput((prevState)=>
    {
        return {...prevState,enteredAmount:e.target.value};
    })
 }

 const dateChangeHandler=(e)=>
 {
    setUserInput((prevState)=>
    {
        return {...prevState,enteredDate:e.target.value};
    })
 }
 //DESTRUCTIONING
 const { enteredTitle, enteredAmount, enteredDate } = userInput;


const submitHandler=(e)=>
{
  e.preventDefault();

  const expenseData={
    title:enteredTitle,
    amount:enteredAmount,
    date:new Date(enteredDate)
  }
  console.log(expenseData);
  props.onSaveExpenseData(expenseData);
  setUserInput({
    enteredTitle: "",
    enteredAmount: "",
    enteredDate: "",
  });
  
}

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" value={enteredTitle} onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" value={enteredAmount} onChange={amountChangeHandler} min="0.01" step="0.01"></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" value={enteredDate} onChange={dateChangeHandler} min="2019-01-01" max="2023-12-31"></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
